from random import randrangefrom math import sqrt# This function randomly generates a center coordinate for a new dot.# The function makes sure that the circle can always be fully displayed on the screen.def get_dot_center(width, height, radius=30):    c_x = randrange(radius, width - radius)    c_y = randrange(radius, height - radius)    return (c_x, c_y)# This function returns true when the euclidean distance between the tip of the index finger # and the center of the dot is smaller than the radius of the dot.def dot_touched(tip_x, tip_y, c_x, c_y, radius):    distance = sqrt((c_x - tip_x) ** 2 + (c_y - tip_y) ** 2)    return distance < radius# This functions returns true if the index finger that first catched the dot originates from the left hand (representing player 1)def is_left_hand(results, index):    return results.multi_handedness[index].classification[0].label == 'Left'